import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BuildingNavigation = () => {
    const [buildings, setBuildings] = useState([]);
    const [rooms, setRooms] = useState([]);
    const [selectedBuilding, setSelectedBuilding] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:5000/api/buildings')
            .then((response) => {
                console.log('Buildings fetched:', response.data); // Debugging line
                setBuildings(response.data);
            })
            .catch((error) => {
                console.error('Error fetching buildings:', error);
            });
    }, []);

    const fetchRooms = (buildingId) => {
      const id = parseInt(buildingId, 10);
      console.log(`Fetching rooms for building ID: ${id}`);
      axios.get(`http://localhost:5000/api/buildings/${id}/rooms`)
          .then((response) => {
              console.log('Rooms fetched:', response.data); // Check the structure
              setRooms(response.data);
              setSelectedBuilding(id);
          })
          .catch((error) => {
              console.error('Error fetching rooms:', error);
          });
  };

    return (
        <nav style={{ backgroundColor: '#333', color: '#fff', padding: '10px' }}>
            <div style={{ fontSize: '1.5rem', marginBottom: '5px' }}>Buildings</div>
            <ul style={{ listStyle: 'none', display: 'flex', gap: '10px' }}>
                {buildings.map((building, index) => {
                    console.log(`Building ID: ${building.id}`); // Debugging line
                    return (
                        <li key={index}>
                            <a href="#" onClick={(e) => { e.preventDefault(); fetchRooms(building.id); }} style={{ color: '#fff', textDecoration: 'none' }}>
                                {building.name}
                            </a>
                        </li>
                    );
                })}
            </ul>
            {selectedBuilding && (
                <div style={{ marginTop: '10px' }}>
                    <h3>Rooms in Building {selectedBuilding}</h3>
                    <ul style={{ listStyle: 'none' }}>
                        {rooms.map((room, index) => (
                            <li key={index} style={{ color: '#ddd' }}>{room.name}</li>
                        ))}
                    </ul>
                </div>
            )}
        </nav>
    );
};

export default BuildingNavigation;
