import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BuildingTab = () => {
  const [buildings, setBuildings] = useState([]);
  const [error, setError] = useState('');

  // Fetch building data on component mount
  useEffect(() => {
    axios.get('http://localhost:5000/api/buildings')
      .then((response) => {
        setBuildings(response.data);
      })
      .catch((err) => {
        setError('Error fetching buildings. Please try again.');
        console.error(err);
      });
  }, []);

  return (
    <div className="building-tab">
      <h2>Buildings</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {buildings.length > 0 ? (
        <ul>
          {buildings.map((building, index) => (
            <li key={index}>
              <strong>{building.name}</strong>: {building.description}
            </li>
          ))}
        </ul>
      ) : (
        <p>No buildings available.</p>
      )}
    </div>
  );
};

export default BuildingTab;
