import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const Sidebar = () => (
  <div className="sidebar">
    <h2>UEcoManage</h2>
    {/* You can add your sidebar links here if needed */}
  </div>
);

const BuildingNavbar = ({ buildings, onSelectBuilding, activeBuilding }) => (
  <nav className="building-navbar">
    {buildings.map((building) => (
      <button
        key={building.id}
        className={activeBuilding === building.name ? "active" : ""}
        onClick={() => onSelectBuilding(building)}
      >
        {building.name}
      </button>
    ))}
  </nav>
);

const BuildingNavigation = () => {
  const [buildings, setBuildings] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [selectedBuilding, setSelectedBuilding] = useState(null);
  const [devices, setDevices] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState(null);

  const [modalBuildingId, setModalBuildingId] = useState('');

  const [showModal, setShowModal] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false); 

  // Modal state
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newRoomName, setNewRoomName] = useState('');

  const [newCamera, setNewCamera] = useState("");  // ← state for live_camera

  const [newBuilding, setNewBuilding] = useState({ name: '', description: '' });

  //  edita modal 
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [roomToEditId, setRoomToEditId] = useState(null);  // Selected room id in the edit modal
  const [editRoomName, setEditRoomName] = useState('');
  const [editCamera, setEditCamera] = useState('');

  const fetchBuildings = () => {
    axios.get('http://localhost:5000/api/buildings')
      .then((response) => {
        setBuildings(response.data);
      })
      .catch((error) => {
        console.error('Error fetching buildings:', error);
      });
  };
  
  // Call the function inside useEffect to load buildings on mount
  useEffect(() => {
    fetchBuildings();
  }, []);

  useEffect(() => {
    if (buildings.length > 0) {
      fetchRooms(buildings[0]);  // Load rooms for the first building on mount
    }
  }, [buildings]);

  const addBuilding = () => {
    axios.post('http://localhost:5000/api/buildings', newBuilding)
      .then(() => {
        fetchBuildings();
        setBuildings([...buildings, newBuilding]);
        setShowAddModal(false);
        setNewBuilding({ name: '', description: '' });
      })
      .catch((error) => {
        console.error('Error adding building:', error);
      });
  };

  const fetchRooms = (building) => {
    axios.get(`http://localhost:5000/api/buildings/${building.id}/rooms`)
      .then((response) => {
        setRooms(response.data);
        setSelectedBuilding(building);
      })
      .catch((error) => {
        console.error('Error fetching rooms:', error);
      });
  };

  const addRoom = () => {
    if (!modalBuildingId) return;
    axios.post(`http://localhost:5000/api/buildings/${modalBuildingId}/rooms`, { name: newRoomName, live_camera: newCamera })
      .then(() => {
        fetchRooms(buildings.find(b => b.id === modalBuildingId));  // Refresh rooms after adding
        setNewRoomName('');
        setNewCamera('');
        setIsModalOpen(false);
      })
      .catch((error) => {
        console.error('Error adding room:', error);
      });
  };

  //update
  const handleUpdateRoom = () => {
    if (!roomToEditId) {
      alert('Please select a room to edit');
      return;
    }
  
    axios.put(`http://localhost:5000/api/rooms/${roomToEditId}`, {
      name: editRoomName,
      live_camera: editCamera,
    })
    .then(() => {
      fetchRooms(selectedBuilding);  // Refresh rooms list after update
      toggleEditModal();
    })
    .catch(err => {
      console.error('Failed to update room', err);
    });
  };
  const fetchDevices = (room) => {
    axios.get(`http://localhost:5000/api/rooms/${room.id}/devices`)
      .then((response) => {
        setDevices(response.data);
        setSelectedRoom(room);
        setShowModal(true);
      })
      .catch((error) => {
        console.error('Error fetching devices:', error);
      });
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedRoom(null);
    setDevices([]);
  };
  const toggleModal = () => setIsModalOpen(!isModalOpen);

  const toggleEditModal = () => {
    setIsEditModalOpen(prev => !prev);
  };

  return (
    <div className="dashboard-container">
      <Sidebar />
      <div className="main">
        <header>
          <h1>UEcoManage Admin</h1>
          <p style={{ color: '#475569', marginTop: '10px' }}>
            Monitor and manage buildings, rooms, and devices.
          </p>

            {/* add building function */}
          <div className="controls-right">
            <button onClick={setShowAddModal}>Add Building</button>
            <button>Edit Building</button>
          </div>
          
          
        </header>

        <BuildingNavbar
          
          onSelectBuilding={fetchRooms}
          buildings={buildings}
          activeBuilding={selectedBuilding}
          onAddBuilding={() => setShowAddModal(true)}
        />

        {/* ADD BUILDING MODAL*/}
        {showAddModal && (
          <div className="modal-overlay">
            <div className="modal">
              <h2>Add Building</h2>
              <input type="text" placeholder="Building Name" value={newBuilding.name} onChange={(e) => setNewBuilding({ ...newBuilding, name: e.target.value })} />
              <input type="text" placeholder="Description" value={newBuilding.description} onChange={(e) => setNewBuilding({ ...newBuilding, description: e.target.value })} />
              <button onClick={addBuilding}>Add Building</button>
              <button onClick={() => setShowAddModal(false)}>Cancel</button>
            </div>
          </div>
        )}

        {selectedBuilding && (
          <section className="rooms">
            <div className="rooms-header">
              <h2>Rooms in {selectedBuilding.name}</h2>             
                {/* add rooms function */}
                <div className="controls-right">
                  <button onClick={toggleModal}>Add Room</button>
                  <button onClick={toggleEditModal}>Edit Room</button>
              </div>
            </div>

            <div className="rooms-grid">
              {rooms.map((room) => (
                <div
                  key={room.id}
                  className="room-card"
                  onClick={() => fetchDevices(room)}
                >
                  {room.name}
                </div>
              ))}
            </div>
          </section>
        )}
        
      {/* Modal for adding a new room */}
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <button className="modal-close" onClick={toggleModal}>×</button>
            <h2>Add New Room</h2>

    {/* Building Selection Dropdown */}
      <select
        value={modalBuildingId}
        onChange={(e) => setModalBuildingId(parseInt(e.target.value))}
      >
        <option value="" disabled>Select Building</option>
        {buildings.map((building) => (
          <option key={building.id} value={building.id}>
            {building.name}
          </option>
        ))}
      </select>

        <input
          type="text"
          value={newRoomName}
          onChange={(e) => {
            const building = buildings.find((b) => b.id === modalBuildingId);
            const prefix = building ? `${building.name} ` : '';
            const inputValue = e.target.value;
        
            // Update the room name only if it starts with the prefix
            if (inputValue.startsWith(prefix)) {
              setNewRoomName(inputValue);
            } else {
              setNewRoomName(prefix);  // Revert to the prefix if the user tries to change it
            }
          }}
          placeholder="Room Name (e.g. EN205)"
        />

        <input
          type="text"
          value={newCamera}
          onChange={(e) => setNewCamera(e.target.value)}
          placeholder="Camera IP (e.g. 192.168.12)"
        />
            <div className="modal-buttons">
              <button className="cancel-btn" onClick={toggleModal}>Cancel</button>
              <button onClick={addRoom}>Add Room</button>
            </div>
          </div>
        </div>
      )}
        {/* Modal edit*/}
        {isEditModalOpen && (
          <div className="modal-overlay">
            <div className="modal">
              <button className="modal-close" onClick={toggleEditModal}>×</button>
              <h2>Edit Room</h2>

              {/* Dropdown to select room */}
              <select
                value={roomToEditId || ''}
                onChange={(e) => {
                  const selectedId = parseInt(e.target.value);
                  setRoomToEditId(selectedId);
                  // Pre-fill edit inputs when room is selected
                  const room = rooms.find(r => r.id === selectedId);
                  if (room) {
                    setEditRoomName(room.name);
                    setEditCamera(room.camera || ''); // Assuming you have camera info
                  }
                }}
              >
                <option value="" disabled>Select Room</option>
                {rooms.map(room => (
                  <option key={room.id} value={room.id}>{room.name}</option>
                ))}
              </select>

              {/* Edit inputs */}
              <input
                type="text"
                value={editRoomName}
                onChange={(e) => setEditRoomName(e.target.value)}
                placeholder="Room Name"
              />
              <input
                type="text"
                value={editCamera}
                onChange={(e) => setEditCamera(e.target.value)}
                placeholder="Camera IP"
              />

              <div className="modal-buttons">
                <button className="cancel-btn" onClick={toggleEditModal}>Cancel</button>
                <button onClick={handleUpdateRoom}>Update Room</button>
              </div>
            </div>
          </div>
        )}
        {/* Modal for Devices */}
        {showModal && selectedRoom && (
          <div className="modal-overlay">
            <div className="modal">
              <button className="modal-close" onClick={closeModal}>×</button>
              <h2>Devices in {selectedRoom.name}</h2>
              <ul style={{ maxHeight: '200px', overflowY: 'auto' }}>
                {devices.length > 0 ? (
                  devices.map((device, i) => <li key={i}>{device.name}</li>)
                ) : (
                  <li>No devices available</li>
                )}
              </ul>
              <div className="modal-buttons">
                <button className="cancel-btn" onClick={closeModal}>Close</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default BuildingNavigation;
