import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';  // Import Link
import axios from 'axios';
import './BuildingTab.css';

// Navbar Component
const Navbar = () => {
  return (
    <nav className="navbar">
      <h2>Admin Dashboard</h2>
      <div className="navbar-links">
        <Link to="/dashboard">Dashboard</Link>
        <Link to="/buildings">Buildings</Link>
        <Link to="/rooms">Rooms</Link>
      </div>
    </nav>
  );
};

// BuildingTab Component
const BuildingTab = () => {
  const [buildings, setBuildings] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/buildings')
      .then((response) => {
        setBuildings(response.data);
      })
      .catch((err) => {
        setError('Error fetching buildings.');
        console.error(err);
      });
  }, []);

  return (
    <div className="building-tab">
      <Navbar /> {/* Include the Navbar here */}
      <h2>Buildings</h2>
      {error && <p className="error">{error}</p>}
      {buildings.length > 0 ? (
        <ul>
          {buildings.map((building, index) => (
            <li key={index}>
              <strong>{building.name}</strong>: {building.description}
            </li>
          ))}
        </ul>
      ) : (
        <p>No buildings found.</p>
      )}
    </div>
  );
};

export { Navbar, BuildingTab };
