import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const BuildingNavigation = () => {
    const [buildings, setBuildings] = useState([]);
    const [rooms, setRooms] = useState([]);
    const [selectedBuilding, setSelectedBuilding] = useState(null);
    const [devices, setDevices] = useState([]);
    const [selectedRoom, setSelectedRoom] = useState(null);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:5000/api/buildings')
            .then((response) => {
                console.log('Buildings fetched:', response.data);
                setBuildings(response.data);
            })
            .catch((error) => {
                console.error('Error fetching buildings:', error);
            });
    }, []);

    const fetchRooms = (buildingId, buildingName) => {
        axios.get(`http://localhost:5000/api/buildings/${buildingId}/rooms`)
            .then((response) => {
                console.log('Rooms fetched:', response.data);
                setRooms(response.data);
                setSelectedBuilding(buildingName);
            })
            .catch((error) => {
                console.error('Error fetching rooms:', error);
            });
    };

    const fetchDevices = (roomId) => {
        axios.get(`http://localhost:5000/api/rooms/${roomId}/devices`)
            .then((response) => {
                console.log('Devices fetched:', response.data);
                setDevices(response.data);
                setShowModal(true);
            })
            .catch((error) => {
                console.error('Error fetching devices:', error);
            });
    };

    const handleRoomClick = (room) => {
        setSelectedRoom(room);
        fetchDevices(room.id);
    };

    const closeModal = () => {
        setShowModal(false);
        setSelectedRoom(null);
        setDevices([]);
    };

    return (
        <nav style={{ backgroundColor: '#333', color: '#fff', padding: '10px' }}>
            <div style={{ fontSize: '1.5rem', marginBottom: '5px' }}>Buildings</div>
            <ul style={{ listStyle: 'none', display: 'flex', gap: '10px' }}>
                {buildings.map((building) => (
                    <li key={building.id}>
                        <a href="#" onClick={(e) => { e.preventDefault(); fetchRooms(building.id, building.name); }} style={{ color: '#fff', textDecoration: 'none' }}>
                            {building.name}
                        </a>
                    </li>
                ))}
            </ul>
            {selectedBuilding && (
                <div style={{ marginTop: '10px' }}>
                    <h3>Rooms in {selectedBuilding}</h3>
                    <ul style={{ listStyle: 'none' }}>
                        {rooms.map((room) => (
                            <li key={room.id} style={{ color: '#ddd', cursor: 'pointer' }} onClick={() => handleRoomClick(room)}>
                                {room.name}
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            {showModal && selectedRoom && (
                <div style={{ position: 'fixed', top: '20%', left: '35%', backgroundColor: '#444', padding: '20px', color: '#fff', borderRadius: '5px', zIndex: 1000 }}>
                    <h3>Devices in {selectedRoom.name}</h3>
                    <ul style={{ listStyle: 'none' }}>
                        {devices.length > 0 ? (
                            devices.map((device, index) => (
                                <li key={index}>{device.name}</li>
                            ))
                        ) : (
                            <li>No devices available</li>
                        )}
                    </ul>
                    <button onClick={closeModal} style={{ marginTop: '10px', backgroundColor: '#555', color: '#fff', padding: '5px 10px', border: 'none', borderRadius: '3px' }}>Close</button>
                </div>
            )}
        </nav>
    );
};

export default BuildingNavigation;
